    //funkcija 'dohvatiDetaljeSastavnice' dohvaća detalje odabrane sastavnice
    public Program dohvatiDetaljeSastavnice(String odabranaSastavnica) {
        SQLiteDatabase db = this.getWritableDatabase();
        Program detalji = new Program(); //spremanje pojedinosti o odabranoj sastavnici

        odabranaSastavnica = DatabaseUtils.sqlEscapeString(odabranaSastavnica); //zbog navodnika u nekim nazivima koji rade probleme prilikom slaganja sql upita i ruše aplikaciju

        String query = "SELECT * FROM programi WHERE sastavnica = " + odabranaSastavnica + " LIMIT 1"; //dohvaća sve podatke ali samo o jednom programu
        Cursor cursor = db.rawQuery(query, null);

        while (cursor.moveToNext()) {
            //potrebni podaci se spremaju u varijablu tipa Program
            detalji.setStudij(cursor.getString(cursor.getColumnIndex(COL_STUDIJ)));
            detalji.setPolje(cursor.getString(cursor.getColumnIndex(COL_POLJE)));
            detalji.setTrajanje(cursor.getDouble(cursor.getColumnIndex(COL_TRAJANJE)));
            detalji.setEcts(cursor.getInt(cursor.getColumnIndex(COL_ECTS)));
            detalji.setTitula(cursor.getString(cursor.getColumnIndex(COL_TITULA)));
            detalji.setUrl(cursor.getString(cursor.getColumnIndex(COL_URL)));
        }

        cursor.close();

        return detalji;
    }


    //olakšava insert u bazu
    public boolean noviProgram(Program program) {
        SQLiteDatabase db = this.getWritableDatabase();

        //pomoćna klasa za rad s bazom i praćenje odgovarajućih tipova podataka
        ContentValues contentValues = new ContentValues();

        //iz argumenta funkcije se čitaju svi potrebni podaci
        contentValues.put(COL_NAZIV, program.getNaziv());
        contentValues.put(COL_NAME, program.getName());
        contentValues.put(COL_TRAJANJE, program.getTrajanje());
        contentValues.put(COL_ECTS, program.getEcts());
        contentValues.put(COL_TITULA, program.getTitula());
        contentValues.put(COL_URL, program.getUrl());
        contentValues.put(COL_STUDIJ, program.getStudij());
        contentValues.put(COL_POLJE, program.getPolje());
        contentValues.put(COL_ZNANOST, program.getZnanost());
        contentValues.put(COL_SASTAVNICA, program.getSastavnica());
        contentValues.put(COL_UCILISTE, program.getUciliste());
        contentValues.put(COL_GRAD, program.getGrad());

        //učitani podaci se insertiraju u bazu
        long result = db.insert(TABLE_NAME, null, contentValues);
        //insert vraća -1 ako nije uspješan, inače vraća id novog unosa
        return result != -1;
    }


